import React from 'react';
import './App.css';

function App() {
  function logcommands(e) {
    fs.readFile(...e.target.files, function read(err, data) {
        if (err) {
            throw err;
        }
        const content = data;

        // Invoke the next step here however you like
        console.log(content);   // Put all of the code here (not the best solution)
    });
  }

  return (
    <div className="App">
     <h1> Tondeuses BimBamJob </h1>
     <input type="file" onChange={e=> { logcommands(e) }}/>
    </div>
  );
}

export default App;
