import React, { useState } from 'react';

import Square from "./square"

class Lawn extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      size: props.size,
      lawn: Array(parseInt(props.size)).fill(null),
    };
  }

  renderLawn() {
    const mower = (
       <svg xmlns="http://www.w3.org/2000/svg" transform="rotate(180)" 
       width="12" height="12" viewBox="0 0 24 24" fill="none" 
       stroke="currentColor" strokeWidth="2" strokeLinecap="round" 
       strokeLinejoin="round" className="mower feather feather-arrow-down">
       <line x1="12" y1="5" x2="12" y2="19"></line><polyline 
       points="19 12 12 19 5 12"></polyline></svg>
     ) 
    return (
        <div>
          {this.state.lawn.map((s,i) => 
            <div key={"r"+i} className="board-row">
            {this.state.lawn.map((sq,index) => 
              <Square /*child={mower}*/ key={index} />
            )}
            </div>
          )}
        </div>
    );
  }

  render() {
    console.log("taille pelouse",this.props.size)
    return (
      <div className="game-board">
        <div className="board-row">
          {this.renderLawn()}
        </div>
      </div>
    )
  }
}

export default Lawn
