import React, { useState, useEffect } from 'react';
import Square from './Square'

const Lawn = (props) =>  {
  const [lawn, setLawn] = useState(Array(parseInt(props.size)).fill(null))

  return (
  	<div>
    <h3>Pelouse taille {props.size}</h3>

          {lawn.map((s,i) => 
            <div key={"r"+i} className="board-row">
            {lawn.map((sq,index) => 
              <Square key={index} />
            )}
            </div>
          )}
    </div>
  );
}
export default Lawn
