import React, { useState } from 'react';

interface FullName {
	name: string;
    x: int;
    y: int;
    s: char;
}

const Mower = (props:FullName) =>  {
  const [position, setPosition] = useState([props.x, props.y, props.s]);
 // console.log("initial position", ...position[0]);

  const commands = "LF"
  const directions = ["N","E","S","W"];

  function changeOrientation(offset) {
  	console.log("Change orientation command ", offset)
  	var length = directions.length
  	var newindex = directions.findIndex(e => e == position[2])+offset
  	console.log("newindex", newindex )

  	// control direction array edges
  	if(newindex == -1) { newindex = length-1}
  	if(newindex == length) { newindex = 0 }

  }

  function readCommands() {
  	var pos = [...position]
  	for(let command of commands) {
  		console.log(command)
   		// Change orientation command 
  		switch(command) {
  			case "R": 
  				pos[2] = changeOrientation(1);
  			break;
  			case "L":
  				pos[2] = changeOrientation(-1);
  			break;
  			default:
  		}
  	}
  	console.log(pos)
  }

  readCommands()

  return (
  	<div>
  		<span>{props.name} </span><br/>
    	<span>{props.x} {props.y} {props.s} </span>
    </div>
  );
}

export default Mower
