import React, { useState, useEffect } from 'react';

import Square from "./square"

const Lawn = (props) => {
  const [size, setSize] = useState(props.size)
  const [lawn, setLawn] = useState(Array(parseInt(size)).fill(null));

  useEffect(() => {     
    setLawn(Array(parseInt(size)).fill(null))

  },[size])

  function renderLawn() {
    const mower = (
       <svg xmlns="http://www.w3.org/2000/svg" transform="rotate(180)" 
       width="12" height="12" viewBox="0 0 24 24" fill="none" 
       stroke="currentColor" strokeWidth="2" strokeLinecap="round" 
       strokeLinejoin="round" className="mower feather feather-arrow-down">
       <line x1="12" y1="5" x2="12" y2="19"></line><polyline 
       points="19 12 12 19 5 12"></polyline></svg>
     ) 
    return (
        <div>
          {lawn.map((s,i) => 
            <div key={"r"+i} className="board-row">
            {lawn.map((sq,index) => 
              <Square /*child={mower}*/ key={index} />
            )}
            </div>
          )}
        </div>
    );
  }




    return (
      <div className="game-board">
        <div className="board-row">
          {renderLawn()}
        </div>
      </div>
    )

}

export default Lawn
