import React, { useState, useEffect } from 'react';
import './App.css';
import Lawn from "./components/Lawn"


import BimBamJob from './components/Bimbamjob'

function App() {
  const [commands, setCommands] = useState()
  const [size, setSize] = useState(0)

  useEffect(() => {     
    var s = String(commands).split(/\r?\n/)[0];
    if(parseInt(s)) {
      setSize(s)
      console.log("update size to", size)
    }

  },[commands])


  function logcommands(e) {
    var fr=new FileReader();
      fr.onload=function(){
          document.getElementById('commands')
                  .textContent=fr.result;
          setCommands(fr.result)
      }

      fr.readAsText(...e.target.files);  
  }

  function generateMowers() {

  }

  return (
    <div className="App">
      <div>
        <h1> Tondeuses BimBamJob </h1>
        <input id="commands" type="file" onChange={e=> { logcommands(e) }}/>
        <textarea id="displaycommands" rows="5" cols="30" value={commands}/>
      </div>
      <div className="game">
         <Lawn size={parseInt(size)} /> 
      </div>


    </div>
  );
}

export default App;
